CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_LISTAR_ARTICULOS`(IN  PIV_CODIGO  			VARCHAR(20),
									 IN  PIV_DESCRIPCION		VARCHAR(100),
                                     IN	 PIV_COD_MARCA			VARCHAR(2),
                                     IN  PIV_COD_TIPO			VARCHAR(2),
									 OUT POV_FLAG_RESULTADO		VARCHAR(10),
									 OUT POV_MENSAJE_RESULTADO	VARCHAR(2000)
									 )
BEGIN
    DECLARE VOV_FLAG_RESULTADO      VARCHAR(10);
    DECLARE VOV_MENSAJE_RESULTADO   VARCHAR(2000);
    DECLARE	VV_QUERY				VARCHAR(8000);
    
    DECLARE EXIT HANDLER FOR SQLEXCEPTION 
    BEGIN
        GET DIAGNOSTICS CONDITION 1
			VOV_FLAG_RESULTADO 		=	RETURNED_SQLSTATE, 
            VOV_MENSAJE_RESULTADO 	=	MESSAGE_TEXT;
        ROLLBACK;
        -- ACTUALIZAMOS LA VARIABLE DE RETORNO: ERROR AL EJCUTAR LA QUERY
        SET POV_MENSAJE_RESULTADO	=	CONCAT('Exception SP_LISTAR_ARTICULOS; CODIGO-->', VOV_FLAG_RESULTADO, '; MENSAJE-->' , VOV_MENSAJE_RESULTADO);
        SET POV_FLAG_RESULTADO      = 	VOV_FLAG_RESULTADO;
    END;

    SET VV_QUERY =
				'   SELECT  ARTI.V_COD_ARTICULO AS "COD_ARTICULO",
							ARTI.V_DESCRIPCION AS "DESCRIPCION",
							ARTI.V_COD_MARCA AS "COD_MARCA",
							(SELECT DAT.V_DESC_DATA
							 FROM TBL_CATALOGO CAT
							 INNER JOIN TBL_DATA_CATALOGO DAT ON (CAT.V_COD_MAESTRO = DAT.FK_V_COD_MAESTRO)
							 WHERE CAT.V_COD_MAESTRO = ''01''
							 AND DAT.V_COD_DATA = ARTI.V_COD_MARCA
							) AS "DESC_MARCA",
							(SELECT DAT.V_DESC_DATA
							 FROM TBL_CATALOGO CAT
							 INNER JOIN TBL_DATA_CATALOGO DAT ON (CAT.V_COD_MAESTRO = DAT.FK_V_COD_MAESTRO)
							 WHERE CAT.V_COD_MAESTRO = ''02''
							 AND DAT.V_COD_DATA = ARTI.V_COD_TIPO  
                             AND DAT.V_COD_DATA_PADRE = ARTI.V_COD_MARCA
                             AND DAT.V_COD_CATALOGO_PADRE = ''01''
							) AS "DESC_TIPO",
							(SELECT DAT.V_DESC_DATA
							 FROM TBL_CATALOGO CAT
							 INNER JOIN TBL_DATA_CATALOGO DAT ON (CAT.V_COD_MAESTRO = DAT.FK_V_COD_MAESTRO)
							 WHERE CAT.V_COD_MAESTRO = ''04''
							 AND DAT.V_COD_DATA = ARTI.V_UND_MEDIDA
							) AS "DESC_UND_MEDIDA"
					FROM TBL_ARTICULO ARTI
					WHERE ARTI.N_ACTIVO = 1
				';
	-- EVALUANDO LAS CONDICIONES
    IF(IFNULL(PIV_CODIGO,'') <> '') THEN
        SET	VV_QUERY = CONCAT(VV_QUERY, ' AND UPPER(ARTI.V_COD_ARTICULO) LIKE UPPER(''%', PIV_CODIGO, '%'')');
    END IF;

    IF(IFNULL(PIV_DESCRIPCION,'') <> '') THEN
        SET	VV_QUERY = CONCAT(VV_QUERY, ' AND ARTI.V_DESCRIPCION LIKE ''%', PIV_DESCRIPCION, '%''');
    END IF;

    IF(IFNULL(PIV_COD_MARCA,'') <> '') THEN
        SET	VV_QUERY = CONCAT(VV_QUERY, ' AND ARTI.V_COD_MARCA = ''', PIV_COD_MARCA, '''');
    END IF;

    IF(IFNULL(PIV_COD_TIPO,'') <> '') THEN
        SET	VV_QUERY = CONCAT(VV_QUERY, ' AND ARTI.V_COD_TIPO = ''', PIV_COD_TIPO, '''');
    END IF;
    
    -- ORDENANDO
    SET	VV_QUERY = CONCAT(VV_QUERY, ' ORDER BY ARTI.V_DESCRIPCION');

	SET @statement = VV_QUERY;
	PREPARE dynquery FROM @statement;
	EXECUTE dynquery;
	DEALLOCATE PREPARE dynquery;
    
    -- ACTUALIZAMOS LA VARIABLE DE RETORNO: EXITO AL EJCUTAR LA QUERY
    SET VOV_MENSAJE_RESULTADO   = NULL;
    SET VOV_FLAG_RESULTADO      = 1;

    SET POV_MENSAJE_RESULTADO   = VOV_MENSAJE_RESULTADO;
    SET POV_FLAG_RESULTADO      = VOV_FLAG_RESULTADO;
  END ;;
DELIMITER ;
