			(SELECT CASE WHEN SN.V_COD_TIPO_DOCUMENTO = '6' THEN SN.V_RAZON_SOCIAL
						 ELSE CONCAT(SN.V_NOMBRES, ' ', SN.V_APE_PATERNO, ' ', SN.V_APE_MATERNO)
					END
			 FROM TBL_SOCIO_NEGOCIOS SN
			 WHERE SN.V_CODIGO_SOCIO_NEGOCIOS = COTIV.FK_V_CODIGO_CLIENTE AND SN.V_COD_TIPO_SOCIO = '01') AS "NOMBRE_CLIENTE",
            (SELECT SN.V_DIRECCION_FISCAL
