            COTIV.V_COD_TIPO_MONEDA AS "COD_TIPO_MONEDA",
			(SELECT DAT.V_DESC_DATA
			 FROM TBL_CATALOGO CAT
			 INNER JOIN TBL_DATA_CATALOGO DAT ON (CAT.V_COD_MAESTRO = DAT.FK_V_COD_MAESTRO)
			 WHERE CAT.V_COD_MAESTRO = '22'
			 AND DAT.V_COD_DATA = COTIV.V_COD_TIPO_MONEDA
			) AS "DESC_TIPO_MONEDA",
			COTIV.V_COD_COND_PAGO AS "COD_COND_PAGO",
			(SELECT DAT.V_DESC_DATA
			 FROM TBL_CATALOGO CAT
			 INNER JOIN TBL_DATA_CATALOGO DAT ON (CAT.V_COD_MAESTRO = DAT.FK_V_COD_MAESTRO)
			 WHERE CAT.V_COD_MAESTRO = '20'
			 AND DAT.V_COD_DATA = COTIV.V_COD_COND_PAGO
			) AS "DESC_COND_PAGO",
            COTIV.V_COD_DIAS AS "COD_DIAS",
            (SELECT DAT.V_DESC_DATA
			 FROM TBL_CATALOGO CAT
			 INNER JOIN TBL_DATA_CATALOGO DAT ON (CAT.V_COD_MAESTRO = DAT.FK_V_COD_MAESTRO)
			 WHERE CAT.V_COD_MAESTRO = '21'
			 AND DAT.V_COD_DATA = COTIV.V_COD_COND_PAGO
			) AS "DESC_COD_DIAS",
            COTIV.V_NRO_REQUERIMIENTO AS "NRO_REQUERIMIENTO",
            COTIV.V_ASUNTO AS "ASUNTO",
            COTIV.V_OBSERVACIONES AS "OBSERVACIONES",
            COTIV.V_NRO_DOCUMENTO_REFERENCIA AS "NRO_DOC_REFERENCIA",
            COTIV.N_PORC_DCTO_TOTAL AS "PORC_DCTO_TOTAL",
